import React, { useState, useEffect } from "react";

const API_BASE = process.env.REACT_APP_API_BASE || "http://localhost:8000";

const CameraForm = ({ onSave, onCancel, initialData = null, onTestSuccess }) => {
  const [formData, setFormData] = useState({
    name: "",
    ip: "",
    username: "",
    password: "",
    port: "554",
  });

  const [testStatus, setTestStatus] = useState(null);
  const [isTesting, setIsTesting] = useState(false);
  const [showPassword, setShowPassword] = useState(false);

  useEffect(() => {
    if (initialData) {
      setFormData({
        name: initialData.name || "",
        ip: initialData.ip || "",
        username: initialData.username || "",
        password: initialData.password || "",
        port: String(initialData.port || "554"),
      });
    } else {
      setFormData({
        name: "",
        ip: "",
        username: "",
        password: "",
        port: "554",
      });
    }
    setTestStatus(null);
  }, [initialData]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  // âœ… NEW: Test = POST /camera/connect (NO RTSP IN FRONTEND)
  const handleTest = async () => {
    const { name, ip, username, password, port } = formData;

    if (!name || !ip || !username || !password) {
      setTestStatus({
        success: false,
        message: "Please fill all required fields",
      });
      return;
    }

    setIsTesting(true);
    setTestStatus({ success: null, message: "Connecting camera..." });

    try {
      const response = await fetch(`${API_BASE}/camera/connect`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          camera_name: name,
          ip,
          username,
          password,
          port: Number(port) || 554,
        }),
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.detail || data.message || "Camera connection failed");
      }

      // âœ… PASS BACKEND RESPONSE UP (camera_id)
      if (typeof onTestSuccess === "function") {
        onTestSuccess({
          id: data.camera_id,
          name,
          ip,
        });
      }

      setTestStatus({
        success: true,
        message: "Camera connected successfully",
      });
    } catch (err) {
      setTestStatus({
        success: false,
        message: err.message || "Camera connection failed",
      });
    } finally {
      setIsTesting(false);
    }
  };

  // âœ… Save should NOT attempt to connect; connect already happened in Test
  const handleSubmit = (e) => {
    e.preventDefault();

    if (!formData.name || !formData.ip) {
      alert("Please fill in Camera Name and IP");
      return;
    }

    onSave({
      name: formData.name,
      ip: formData.ip,
    });
  };

  return (
    <form className="camera-form" onSubmit={handleSubmit}>
      <div className="form-group">
        <label>Camera Name</label>
        <input
          type="text"
          name="name"
          value={formData.name}
          onChange={handleChange}
          placeholder="Enter camera name"
          required
        />
      </div>

      <div className="form-group">
        <label>Camera IP</label>
        <input
          type="text"
          name="ip"
          value={formData.ip}
          onChange={handleChange}
          placeholder="Enter camera ip"
          required
        />
      </div>

      <div className="form-group">
        <label>Username</label>
        <input
          type="text"
          name="username"
          value={formData.username}
          onChange={handleChange}
          placeholder="Enter camera username"
          required
        />
      </div>

      <div className="form-group">
        <label>Password</label>
        <div className="password-input-wrapper">
          <input
            type={showPassword ? "text" : "password"}
            name="password"
            value={formData.password}
            onChange={handleChange}
            required
          />
          <button
            type="button"
            className="password-toggle"
            onClick={() => setShowPassword((prev) => !prev)}
          >
            {showPassword ? "ðŸ”“" : "ðŸ”’"}
          </button>
        </div>
      </div>

      <div className="form-group">
        <label>Port</label>
        <input
          type="number"
          name="port"
          value={formData.port}
          onChange={handleChange}
          min="1"
          max="65535"
          placeholder="554"
          required
          style={{ backgroundColor: "#f9f9f9" }}
        />
      </div>

      <div className={`status-message-area ${testStatus ? "has-status" : ""}`}>
        {testStatus && (
          <div className="status-message-box">
            {testStatus.success === true ? (
              <span className="status-success">
                <span style={{ fontSize: "16px" }}>âœ”</span>
                {testStatus.message}
              </span>
            ) : testStatus.success === false ? (
              <span className="status-error">
                <span style={{ fontSize: "16px" }}>âœ˜</span>
                {testStatus.message}
              </span>
            ) : (
              <span>{testStatus.message}</span>
            )}
          </div>
        )}
      </div>

      <div className="form-actions">
        <button
          type="button"
          className="test-btn"
          onClick={handleTest}
          disabled={isTesting}
        >
          Test
        </button>

        <button type="button" className="cancel-btn" onClick={onCancel}>
          Cancel
        </button>

        <button type="submit" className="save-btn">
          Save
        </button>
      </div>
    </form>
  );
};

export default CameraForm;
