import React, { useState, useEffect, useRef } from "react";

const CameraItem = ({ camera, onStartFeed, onStopFeed, onEdit, onDelete }) => {
  const [showMenu, setShowMenu] = useState(false);
  const menuRef = useRef(null);

  // Close menu when clicking outside
  useEffect(() => {
    const close = (e) => {
      if (menuRef.current && !menuRef.current.contains(e.target)) {
        setShowMenu(false);
      }
    };
    document.addEventListener("mousedown", close);
    return () => document.removeEventListener("mousedown", close);
  }, []);

  return (
    <div className="camera-item" ref={menuRef}>
      <div className="camera-info">
        <div className="camera-name">{camera.name}</div>
        <div className="camera-ip">{camera.ip}</div>
      </div>

      <div className="camera-controls">
        {!camera.isLive ? (
          <button
            className="control-btn start-btn"
            onClick={() => onStartFeed(camera)}
          >
            ▶
          </button>
        ) : (
          <button
            className="control-btn stop-btn"
            onClick={() => onStopFeed(camera.id)}
          >
            ■
          </button>
        )}

        <div className="dropdown-menu">
          <button
            className="control-btn menu-btn"
            onClick={() => setShowMenu(!showMenu)}
          >
            ⋮
          </button>

          {showMenu && (
            <div className="menu-content">
              <button onClick={() => onEdit(camera.id, camera)}>Edit</button>
              <button onClick={() => onDelete(camera.id)}>Delete</button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default CameraItem;
