import React, { useState } from 'react';

const CameraItem = ({ camera, onStartFeed, onStopFeed, onEdit, onDelete }) => {
  const [showMenu, setShowMenu] = useState(false);
  const [isEditing, setIsEditing] = useState(false);
  const [editData, setEditData] = useState({ ...camera });

  const toggleMenu = () => {
    setShowMenu(!showMenu);
  };

  const handleEdit = () => {
    setIsEditing(true);
    setShowMenu(false);
  };

  const handleSaveEdit = () => {
    onEdit(camera.id, editData);
    setIsEditing(false);
  };

  const handleCancelEdit = () => {
    setEditData({ ...camera });
    setIsEditing(false);
  };

  const handleDelete = () => {
    if (window.confirm('Are you sure you want to delete this camera?')) {
      onDelete(camera.id);
    }
    setShowMenu(false);
  };

  return (
    <div className="camera-item">
      {isEditing ? (
        <>
          <div style={{ flex: 1 }}>
            <input
              type="text"
              value={editData.name}
              onChange={(e) => setEditData({ ...editData, name: e.target.value })}
              style={{ width: '100%', marginBottom: '0.5rem' }}
            />
            <input
              type="text"
              value={editData.ip}
              onChange={(e) => setEditData({ ...editData, ip: e.target.value })}
              style={{ width: '100%' }}
            />
          </div>
          <div className="camera-controls">
            <button onClick={handleSaveEdit} className="control-btn start-btn">✓</button>
            <button onClick={handleCancelEdit} className="control-btn stop-btn">✗</button>
          </div>
        </>
      ) : (
        <>
          <div className="camera-info">
            <div className="camera-name">{camera.name}</div>
            <div className="camera-ip">{camera.ip}</div>
          </div>
          <div className="camera-controls">
            {!camera.isLive ? (
              <button
                onClick={() => onStartFeed(camera)}
                className="control-btn start-btn"
                title="Start Feed"
              >
                ▶
              </button>
            ) : (
              <button
                onClick={() => onStopFeed(camera.id)}
                className="control-btn stop-btn"
                title="Stop Feed"
              >
                ■
              </button>
            )}
            <div className="dropdown-menu">
              <button
                onClick={toggleMenu}
                className="control-btn menu-btn"
                title="Menu"
              >
                ⋮
              </button>
              {showMenu && (
                <div className="menu-content">
                  <button onClick={handleEdit} className="menu-item edit-item">
                    Edit
                  </button>
                  <button onClick={handleDelete} className="menu-item delete-item">
                    Delete
                  </button>
                </div>
              )}
            </div>
          </div>
        </>
      )}
    </div>
  );
};

export default CameraItem;
