import React, { useState } from 'react';
import Header from './components/Header';
import CameraForm from './components/CameraForm';
import CameraList from './components/CameraList';
import VideoPreview from './components/VideoPreview';
import AboutModal from './components/AboutModal';
import './App.css';

function App() {
  const [cameras, setCameras] = useState([]);
  const [selectedCamera, setSelectedCamera] = useState(null);
  const [showAboutModal, setShowAboutModal] = useState(false);
  const [showAddForm, setShowAddForm] = useState(false);

  const handleAddCamera = (cameraData) => {
    const newCamera = {
      ...cameraData,
      id: Date.now(),
      isLive: false
    };
    setCameras([...cameras, newCamera]);
    setShowAddForm(false); // Hide the form after saving
  };

  const handleStartFeed = (camera) => {
    setCameras(
      cameras.map((c) => ({
        ...c,
        isLive: c.id === camera.id,
      }))
    );
    setSelectedCamera(camera);
  };

  const handleStopFeed = (cameraId) => {
    setCameras(
      cameras.map((c) =>
        c.id === cameraId ? { ...c, isLive: false } : c
      )
    );
    if (selectedCamera?.id === cameraId) setSelectedCamera(null);
  };

  return (
    <div className="app">
      <Header onAboutClick={() => setShowAboutModal(true)} />

      <AboutModal
        isOpen={showAboutModal}
        onClose={() => setShowAboutModal(false)}
      />

      <div className="main-content">
        
        {/* LEFT PANEL */}
        <div className="left-panel">
          <div className="section-header">
            <h2>Attached Cameras</h2>
            <div className="cameras-count">{cameras.length} cameras</div>
          </div>

          {cameras.length === 0 ? (
            <div className="empty-message">
              <p>No cameras added.</p>
              <p>Click "Add Camera" to get started.</p>
            </div>
          ) : (
            <CameraList
              cameras={cameras}
              onStartFeed={handleStartFeed}
              onStopFeed={handleStopFeed}
            />
          )}
        </div>

        {/* CENTER VIDEO */}
        <VideoPreview
          camera={selectedCamera}
          isLive={selectedCamera?.isLive}
        />

        {/* RIGHT PANEL */}
        <div className="right-panel">
          <div className="add-camera-card">
            <h2 className="add-camera-title">Add Camera</h2>

            {!showAddForm ? (
              <div
                className="add-camera-trigger-right"
                onClick={() => setShowAddForm(true)}
              >
                <div className="add-icon-right">+</div>
              </div>
            ) : (
              <CameraForm
                onSave={handleAddCamera}
                onCancel={() => setShowAddForm(false)}
              />
            )}
          </div>
        </div>

      </div>
    </div>
  );
}

export default App;
