import React, { useEffect, useState } from "react";
import LoginScreen from "./LoginScreen";
import Header from "./components/Header";
import AboutModal from "./components/AboutModal";
import CameraForm from "./components/CameraForm";
import CameraList from "./components/CameraList";
import VideoPreview from "./components/VideoPreview";
import "./App.css";

const API_BASE = process.env.REACT_APP_API_BASE || "http://localhost:8000";

const AuthenticatedApp = () => {
  const [cameras, setCameras] = useState([]);
  const [selectedCamera, setSelectedCamera] = useState(null);
  const [showAboutModal, setShowAboutModal] = useState(false);
  const [showAddForm, setShowAddForm] = useState(false);
  const [editingCamera, setEditingCamera] = useState(null);
  const [previewCamera, setPreviewCamera] = useState(null);
  const [isPreviewLive, setIsPreviewLive] = useState(false);

  // ✅ Load cameras from backend
  const loadCameras = async () => {
    try {
      const res = await fetch(`${API_BASE}/camera/list`);
      if (!res.ok) return;

      const data = await res.json();

      // Support common response shapes
      const list = Array.isArray(data)
        ? data
        : Array.isArray(data.cameras)
        ? data.cameras
        : Array.isArray(data.data)
        ? data.data
        : [];

      const normalized = list
        .map((c) => ({
          id: c.id ?? c.camera_id ?? c.cameraId,
          name: c.name ?? c.camera_name ?? c.cameraName,
          ip: c.ip,
          isLive: false,
        }))
        .filter((c) => c.id != null);

      setCameras(normalized);
    } catch (e) {
      console.error("Failed to load camera list:", e);
    }
  };

  useEffect(() => {
    loadCameras();
  }, []);

  const handleOpenAddForm = () => {
    setEditingCamera(null);
    setShowAddForm(true);
  };

  // ✅ Save only updates UI fields (connect already happened during Test)
  const handleSaveCamera = (formData) => {
    if (editingCamera) {
      setCameras((prev) =>
        prev.map((cam) =>
          cam.id === editingCamera.id
            ? { ...cam, name: formData.name, ip: formData.ip }
            : cam
        )
      );

      if (selectedCamera && selectedCamera.id === editingCamera.id) {
        setSelectedCamera((prev) => ({ ...prev, name: formData.name, ip: formData.ip }));
      }

      if (previewCamera && previewCamera.id === editingCamera.id) {
        setPreviewCamera((prev) => ({ ...prev, name: formData.name, ip: formData.ip }));
      }
    }

    setEditingCamera(null);
    setShowAddForm(false);
  };

  const handleCancelForm = () => {
    setEditingCamera(null);
    setShowAddForm(false);
  };

  // ✅ NEW: When Test succeeds, backend returns camera_id → add/select/live preview
  const handleTestSuccess = (camera) => {
    const newCamera = {
      id: camera.id,
      name: camera.name,
      ip: camera.ip,
      isLive: true,
    };

    setCameras((prev) => {
      const exists = prev.some((c) => c.id === newCamera.id);
      if (exists) {
        return prev.map((c) => (c.id === newCamera.id ? { ...c, ...newCamera } : c));
      }
      return [...prev, newCamera];
    });

    setSelectedCamera(newCamera);
    setPreviewCamera(newCamera);
    setIsPreviewLive(true);

    // Optional refresh to stay in sync with backend
    loadCameras();
  };

  const handleEditCamera = (camera) => {
    setEditingCamera(camera);
    setShowAddForm(true);
  };

  // No backend delete endpoint in your current contract, keep UI delete only
  const handleDeleteCamera = async (cameraId) => {
    if (!window.confirm("Are you sure you want to delete this camera (UI only)?")) return;

    setCameras((prev) => prev.filter((cam) => cam.id !== cameraId));

    if (selectedCamera && selectedCamera.id === cameraId) setSelectedCamera(null);
    if (previewCamera && previewCamera.id === cameraId) {
      setPreviewCamera(null);
      setIsPreviewLive(false);
    }
  };

  // ✅ Start/Stop now only control preview selection (stream is GET /stream/{id})
  const startPreview = (cam) => {
    setPreviewCamera(cam);
    setIsPreviewLive(true);
    setSelectedCamera(cam);

    setCameras((prev) => prev.map((c) => ({ ...c, isLive: c.id === cam.id })));
  };

  const stopPreview = () => {
    setPreviewCamera(null);
    setIsPreviewLive(false);

    setCameras((prev) => prev.map((c) => ({ ...c, isLive: false })));
  };

  return (
    <div className="app">
      <Header onAboutClick={() => setShowAboutModal(true)} />

      <AboutModal isOpen={showAboutModal} onClose={() => setShowAboutModal(false)} />

      <div className="main-content">
        {/* LEFT PANEL */}
        <div className="left-panel">
          <div className="section-header">
            <h2>Attached Cameras</h2>
            <div className="cameras-count">{cameras.length} cameras</div>
          </div>

          {cameras.length === 0 ? (
            <div className="empty-state">
              <p>No cameras added. Click "Add Camera" to get started.</p>
            </div>
          ) : (
            <CameraList
              cameras={cameras}
              onStartFeed={(cam) => startPreview(cam)}
              onStopFeed={() => stopPreview()}
              onEdit={handleEditCamera}
              onDelete={handleDeleteCamera}
            />
          )}
        </div>

        {/* CENTER PREVIEW */}
        <div className="middle-panel">
          <VideoPreview camera={previewCamera} isLive={isPreviewLive} apiBase={API_BASE} />
        </div>

        {/* RIGHT PANEL */}
        <div className="right-panel">
          <div className="add-camera-card">
            <div className="add-camera-header">
              <h2 className="add-camera-title">{editingCamera ? "Edit Camera" : "Add Camera"}</h2>

              {!showAddForm && (
                <div className="add-camera-trigger-right" onClick={handleOpenAddForm}>
                  <div className="add-icon-right">+</div>
                </div>
              )}
            </div>

            {showAddForm && (
              <CameraForm
                onSave={handleSaveCamera}
                onCancel={handleCancelForm}
                initialData={editingCamera}
                onTestSuccess={handleTestSuccess}
              />
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

const App = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  const VALID_USERNAME = "Grow";
  const VALID_PASSWORD = "123123";

  const handleLogin = (username, password) => {
    if (username === VALID_USERNAME && password === VALID_PASSWORD) {
      setIsAuthenticated(true);
    } else {
      alert("Invalid username or password");
    }
  };

  if (!isAuthenticated) {
    return <LoginScreen onLogin={handleLogin} />;
  }

  return <AuthenticatedApp />;
};

export default App;
