import React, { useState, useEffect } from "react";

const API_BASE = "http://localhost:8000";

const CameraForm = ({ onSave, onCancel, initialData = null, onTestSuccess }) => {
  const [formData, setFormData] = useState({
    name: "",
    ip: "172.18.",
    username: "",
    password: "",
    port: "554",
    rtspUrl: "",
  });

  const [testStatus, setTestStatus] = useState(null);
  const [isTesting, setIsTesting] = useState(false);
  const [showPassword, setShowPassword] = useState(false);

  useEffect(() => {
    if (initialData) {
      const merged = {
        name: initialData.name || "",
        ip: initialData.ip || "172.18.",
        username: initialData.username || "",
        password: initialData.password || "",
        port: "554",
        rtspUrl: "",
      };
      merged.rtspUrl = generateRTSPUrl(merged);
      setFormData(merged);
    } else {
      const fresh = {
        name: "",
        ip: "172.18.",
        username: "",
        password: "",
        port: "554",
        rtspUrl: "",
      };
      fresh.rtspUrl = generateRTSPUrl(fresh);
      setFormData(fresh);
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [initialData]);

  const generateRTSPUrl = (data) => {
    const { username, password, ip } = data;
    if (username && password && ip) {
      const encodedUsername = encodeURIComponent(username);
      const encodedPassword = encodeURIComponent(password);
      // HARD-LOCK RTSP PORT = 554 (prevents accidental 8000)
      return `rtsp://${encodedUsername}:${encodedPassword}@${ip}:554/Streaming/Channels/101`;
    }
    return "";
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    const updated = { ...formData, [name]: value };

    if (["username", "password", "ip"].includes(name)) {
      updated.rtspUrl = generateRTSPUrl(updated);
    }

    setFormData(updated);
  };

  const handleRefreshUrl = () => {
    const newUrl = generateRTSPUrl(formData);
    setFormData({ ...formData, rtspUrl: newUrl });
  };

  const handleTest = async () => {
    const { username, password, rtspUrl } = formData;

    if (!username || !password || !rtspUrl) {
      setTestStatus({ success: false, message: "Please enter Username, Password and IP." });
      return;
    }

    setIsTesting(true);
    setTestStatus({ success: null, message: "Testing connection..." });

    try {
      const response = await fetch(`${API_BASE}/test-camera`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ rtspUrl }),
      });

      const data = await response.json();

      if (data.success) {
        setTestStatus({ success: true, message: data.message || "Connected Successfully" });
        if (typeof onTestSuccess === "function") onTestSuccess(formData);
      } else {
        setTestStatus({ success: false, message: data.message || "Error: Not Connected" });
      }
    } catch (error) {
      setTestStatus({ success: false, message: "Server error: Unable to test camera" });
    } finally {
      setIsTesting(false);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!formData.name || !formData.ip || !formData.rtspUrl) {
      alert("Please fill in all required fields");
      return;
    }
    onSave(formData);
  };

  return (
    <form className="camera-form" onSubmit={handleSubmit}>
      <div className="form-group">
        <label>Camera Name</label>
        <input
          type="text"
          name="name"
          value={formData.name}
          onChange={handleChange}
          placeholder="Enter camera name"
          required
        />
      </div>

      <div className="form-group">
        <label>Camera IP</label>
        <input
          type="text"
          name="ip"
          value={formData.ip}
          onChange={handleChange}
          placeholder="172.18.11.108"
          required
        />
      </div>

      <div className="form-group">
        <label>Username</label>
        <input type="text" name="username" value={formData.username} onChange={handleChange} required />
      </div>

      <div className="form-group">
        <label>Password</label>
        <div className="password-input-wrapper">
          <input
            type={showPassword ? "text" : "password"}
            name="password"
            value={formData.password}
            onChange={handleChange}
            required
          />
          <button
            type="button"
            className="password-toggle"
            onClick={() => setShowPassword((prev) => !prev)}
          >
            {showPassword ? "ðŸ”“" : "ðŸ”’"}
          </button>
        </div>
      </div>

      <div className="form-group">
        <label>Port</label>
        <input
          type="text"
          name="port"
          value="554"
          readOnly
          style={{ backgroundColor: "#f5f5f5", cursor: "not-allowed" }}
        />
        <div style={{ fontSize: "12px", color: "#666", marginTop: "4px" }}>
          RTSP port is locked to 554 to prevent accidental misconfiguration.
        </div>
      </div>

      <div className="form-group">
        <label>RTSP URL</label>
        <div className="url-group">
          <input
            type="text"
            name="rtspUrl"
            value={formData.rtspUrl}
            readOnly
            required
            style={{ backgroundColor: "#f5f5f5", cursor: "not-allowed" }}
          />
          <button type="button" className="refresh-btn" onClick={handleRefreshUrl} title="Generate RTSP URL">
            Refresh
          </button>
        </div>
      </div>

      <div className={`status-message-area ${testStatus ? "has-status" : ""}`}>
        {testStatus && (
          <div className="status-message-box">
            {testStatus.success === true ? (
              <span className="status-success">âœ” {testStatus.message}</span>
            ) : testStatus.success === false ? (
              <span className="status-error">âœ˜ {testStatus.message}</span>
            ) : (
              <span>{testStatus.message}</span>
            )}
          </div>
        )}
      </div>

      <div className="form-actions">
        <button type="button" className="test-btn" onClick={handleTest} disabled={isTesting || !formData.rtspUrl}>
          Test
        </button>
        <button type="button" className="cancel-btn" onClick={onCancel}>
          Cancel
        </button>
        <button type="submit" className="save-btn">
          Save
        </button>
      </div>
    </form>
  );
};

export default CameraForm;
