import React from 'react';

const API_BASE = 'http://localhost:8000';

const VideoPreview = ({ camera, isLive }) => {
  const streamUrl =
    camera && isLive
      ? `${API_BASE}/stream?rtsp=${encodeURIComponent(camera.rtspUrl)}`
      : null;

  return (
    <div className="video-preview">
      <div className="video-header">
        <div className="video-title">
          {camera ? camera.name : 'No Camera Selected'}
        </div>
        {isLive && <div className="live-badge">LIVE</div>}
      </div>

      <div className="video-placeholder">
        {streamUrl ? (
          <img
            src={streamUrl}
            alt={`Live feed from ${camera.name}`}
            style={{ width: '100%', height: '100%', objectFit: 'cover' }}
          />
        ) : camera ? (
          <div style={{ color: '#666' }}>
            Click the green play button to start live feed
          </div>
        ) : (
          <div style={{ color: '#666' }}>
            Select a camera and click the play button to view live feed
          </div>
        )}
      </div>
    </div>
  );
};

export default VideoPreview;
