import React from "react";

const VideoPreview = ({ camera, isLive, apiBase }) => {
  // If there's a camera and it's live, construct the stream URL for the preview
  const streamUrl = camera && isLive
    ? `${apiBase}/preview-stream?rtspUrl=${encodeURIComponent(camera.rtspUrl)}`
    : null;

  return (
    <div className="video-preview">
      <div className="video-header">
        <div className="video-title">
          {camera ? camera.name : "No Camera Selected"}
        </div>
        {isLive && <div className="live-badge">LIVE</div>}
      </div>

      <div className="video-placeholder">
        {streamUrl ? (
          <img
            src={streamUrl}
            alt="camera-live"
            style={{ width: "100%", height: "100%", objectFit: "cover" }}
          />
        ) : camera ? (
          <div style={{ color: "#777" }}>Click Test to check camera connection</div>
        ) : (
          <div style={{ color: "#777" }}>
            Select a camera to preview live feed
          </div>
        )}
      </div>
    </div>
  );
};

export default VideoPreview;
