import React, { useState, useEffect } from "react";

const API_BASE = "http://localhost:8000";

const CameraForm = ({ onSave, onCancel, initialData = null, onTestSuccess }) => {
  const [formData, setFormData] = useState({
    name: "",
    ip: "192.168.150.", // Common IP pattern
    username: "admin",
    password: "",
    port: "554",
    rtspUrl: "",
  });

  const [testStatus, setTestStatus] = useState(null);
  const [isTesting, setIsTesting] = useState(false);
  const [showPassword, setShowPassword] = useState(false);

  useEffect(() => {
    if (initialData) {
      setFormData({
        name: initialData.name || "",
        ip: initialData.ip || "192.168.150.",
        username: initialData.username || "admin",
        password: initialData.password || "",
        port: initialData.port || "554",
        rtspUrl: initialData.rtspUrl || "",
      });
    } else {
      setFormData({
        name: "",
        ip: "192.168.150.",
        username: "admin",
        password: "",
        port: "554",
        rtspUrl: "",
      });
    }
  }, [initialData]);

  const generateRTSPUrl = (data) => {
    const { username, password, ip, port } = data;
    if (username && password && ip && port) {
      // ENCODE username and password for RTSP URL
      const encodedUsername = encodeURIComponent(username);
      const encodedPassword = encodeURIComponent(password);
      // Common Hikvision RTSP URL format
      return `rtsp://${encodedUsername}:${encodedPassword}@${ip}:${port}/Streaming/Channels/101`;
    }
    return "";
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    const updated = { ...formData, [name]: value };

    if (["username", "password", "ip", "port"].includes(name)) {
      updated.rtspUrl = generateRTSPUrl(updated);
    }

    setFormData(updated);
  };

  const handleRefreshUrl = () => {
    const newUrl = generateRTSPUrl(formData);
    if (newUrl) {
      setFormData({ ...formData, rtspUrl: newUrl });
      setTestStatus({
        success: true,
        message: "RTSP URL generated successfully"
      });
    }
  };

  const handleTest = async () => {
    const { username, password, rtspUrl, port } = formData;

    if (!username || !password) {
      setTestStatus({
        success: false,
        message: "Please enter Username and Password",
      });
      return;
    }

    if (!rtspUrl) {
      setTestStatus({
        success: false,
        message: "RTSP URL is empty. Click the refresh button to generate.",
      });
      return;
    }

    if (port === "8000") {
      setTestStatus({
        success: false,
        message: "Port 8000 is for the web server. Camera RTSP typically uses port 554.",
      });
      return;
    }

    if (!rtspUrl.toLowerCase().startsWith("rtsp://")) {
      setTestStatus({
        success: false,
        message: "Invalid RTSP URL. It must start with rtsp://",
      });
      return;
    }

    setIsTesting(true);
    setTestStatus({
      success: null,
      message: "Testing camera connection...",
    });

    try {
      const response = await fetch(`${API_BASE}/test-camera`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          rtspUrl: formData.rtspUrl,
        }),
      });

      const data = await response.json();

      if (response.ok && data.success) {
        setTestStatus({
          success: true,
          message: data.message || "Connected Successfully",
        });

        if (typeof onTestSuccess === "function") {
          onTestSuccess(formData);
        }
      } else {
        setTestStatus({
          success: false,
          message: data.message || "Error: Unable to connect to camera",
        });
      }
    } catch (error) {
      setTestStatus({
        success: false,
        message: "Server error: Unable to test camera connection",
      });
    } finally {
      setIsTesting(false);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    
    // Final validation
    if (!formData.name) {
      alert("Please enter a Camera Name");
      return;
    }
    
    if (!formData.ip || formData.ip === "192.168.150.") {
      alert("Please enter a valid Camera IP address");
      return;
    }
    
    if (formData.port === "8000") {
      alert("Port 8000 is for the web server. Camera RTSP typically uses port 554.");
      return;
    }
    
    if (!formData.rtspUrl) {
      alert("Please generate RTSP URL by clicking the refresh button");
      return;
    }
    
    onSave(formData);
  };

  return (
    <form className="camera-form" onSubmit={handleSubmit}>
      <div className="form-group">
        <label>Camera Name *</label>
        <input
          type="text"
          name="name"
          value={formData.name}
          onChange={handleChange}
          placeholder="Enter camera name (e.g., Gate Camera, Entrance)"
          required
        />
      </div>

      <div className="form-group">
        <label>Camera IP Address *</label>
        <input
          type="text"
          name="ip"
          value={formData.ip}
          onChange={handleChange}
          placeholder="192.168.150.108"
          required
        />
        <small className="form-hint">Format: 192.168.x.x (Common for local cameras)</small>
      </div>

      <div className="form-row">
        <div className="form-group">
          <label>Username *</label>
          <input
            type="text"
            name="username"
            value={formData.username}
            onChange={handleChange}
            placeholder="admin"
            required
          />
        </div>

        <div className="form-group">
          <label>Password *</label>
          <div className="password-input-wrapper">
            <input
              type={showPassword ? "text" : "password"}
              name="password"
              value={formData.password}
              onChange={handleChange}
              placeholder="Enter camera password"
              required
            />
            <button
              type="button"
              className="password-toggle"
              onClick={() => setShowPassword((prev) => !prev)}
              title={showPassword ? "Hide password" : "Show password"}
            >
              {showPassword ? "üôà" : "üëÅÔ∏è"}
            </button>
          </div>
        </div>
      </div>

      <div className="form-group">
        <label>RTSP Port *</label>
        <input
          type="number"
          name="port"
          value={formData.port}
          onChange={handleChange}
          min="1"
          max="65535"
          placeholder="554"
          required
        />
        <small className="form-hint">
          Standard RTSP port: 554. Port 8000 is reserved for this web server.
          {formData.port === "8000" && (
            <span style={{ color: "red", fontWeight: "bold" }}> ‚ö†Ô∏è Warning: Port 8000 is for web server, not cameras!</span>
          )}
        </small>
      </div>

      <div className="form-group">
        <label>RTSP URL *</label>
        <div className="url-group">
          <input
            type="text"
            name="rtspUrl"
            value={formData.rtspUrl}
            onChange={handleChange}
            placeholder="rtsp://admin:password@192.168.x.x:554/..."
            required
            readOnly
            className="rtsp-url-input"
          />
          <button
            type="button"
            className="refresh-btn"
            onClick={handleRefreshUrl}
            title="Generate RTSP URL from credentials"
            disabled={!formData.username || !formData.password || !formData.ip || !formData.port}
          >
            <svg className="refresh-icon" viewBox="0 0 24 24" width="20" height="20">
              <path
                fill="currentColor"
                d="M17.65,6.35C16.2,4.9 14.21,4 12,4A8,8 0 0,0 4,12A8,8 0 0,0 12,20C15.73,20 18.84,17.45 19.73,14H17.65C16.83,16.33 14.61,18 12,18A6,6 0 0,1 6,12A6,6 0 0,1 12,6C13.66,6 15.14,6.69 16.22,7.78L13,11H20V4L17.65,6.35Z"
              />
            </svg>
            <span>Generate</span>
          </button>
        </div>
        <small className="form-hint">
          URL is auto-generated from the fields above. Common format for Hikvision cameras.
        </small>
      </div>

      {testStatus && (
        <div className={`test-status ${testStatus.success === true ? 'success' : testStatus.success === false ? 'error' : 'info'}`}>
          {testStatus.success === true ? (
            <div className="status-message">
              <span className="status-icon">‚úÖ</span>
              <span className="status-text">{testStatus.message}</span>
            </div>
          ) : testStatus.success === false ? (
            <div className="status-message">
              <span className="status-icon">‚ùå</span>
              <span className="status-text">{testStatus.message}</span>
            </div>
          ) : (
            <div className="status-message">
              <span className="status-icon">‚è≥</span>
              <span className="status-text">{testStatus.message}</span>
            </div>
          )}
        </div>
      )}

      <div className="form-actions">
        <button
          type="button"
          className="btn-test"
          onClick={handleTest}
          disabled={isTesting || !formData.rtspUrl}
        >
          {isTesting ? (
            <>
              <span className="spinner"></span>
              Testing...
            </>
          ) : (
            "Test Connection"
          )}
        </button>
        <button type="button" className="btn-cancel" onClick={onCancel}>
          Cancel
        </button>
        <button type="submit" className="btn-save" disabled={!formData.rtspUrl}>
          Save Camera
        </button>
      </div>
    </form>
  );
};

export default CameraForm;
